INSERT INTO `mr_article` (`id`, `isread`, `weid`, `type`, `pcate`, `ccate`, `title`, `content`, `thumb`, `source`, `author`, `createtime`, `cool`) VALUES
(95, 0, 3, ',', 47, 81, '&lt;&lt;lee减压法&gt;&gt;', '<p class="MsoNormal">\r\n	<span style="font-family:宋体;color:#666666;font-size:16px;"><img src="/las/resource/attachment/images/3/2014/03/MIkcnz23iN9Dz0220nhqC799ZDdQFz.jpg" alt="" /><br />\r\n</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;color:#666666;font-size:16px;">作者：</span><span style="font-size:16px;font-family:微软雅黑;color:#404040;">[</span><span style="font-size:9.0pt;font-family:微软雅黑;color:#404040;"><span style="font-size:16px;">美</span><span><span style="font-size:16px;">] </span><a href="http://search.dangdang.com/?key2=%C2%DE%B2%AE%CB%FE&medium=01&category_path=01.00.00.00.00.00" target="_blank"><span style="font-size:16px;"><span style="font-size:16px;"><span style="font-size:16px;">罗伯塔</span></span></span><span></span></a><span style="font-size:16px;">?</span></span><span style="font-size:16px;">李（</span></span><span></span><a href="http://search.dangdang.com/?key2=Roberta&medium=01&category_path=01.00.00.00.00.00" target="_blank"><span style="font-size:16px;"><span style="font-size:16px;font-family:微软雅黑;">Roberta</span></span><span></span></a><span></span><span style="font-size:16px;font-family:微软雅黑;color:#404040;"> Lee</span><span style="font-size:16px;font-family:微软雅黑;color:#404040;">）　著</span><span style="color:#666666;"></span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="color:#666666;font-size:16px;"> </span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;color:#666666;font-size:16px;">内容简介：</span><span style="color:#666666;"></span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="color:#666666;font-size:16px;"> </span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;color:#666666;font-size:16px;">《</span><span style="color:#666666;font-size:16px;">Lee</span><span style="font-family:宋体;color:#666666;font-size:16px;">博士减压法》是克林顿健康咨询专家、美国辅助医学界领军人物联袂推荐的科学减压手册。</span><span style="color:#666666;"><br />\r\n</span><span style="font-family:宋体;color:#666666;font-size:16px;"> 《</span><span style="color:#666666;font-size:16px;">Lee</span><span style="font-family:宋体;color:#666666;font-size:16px;">博士减压法》是作者</span><span style="color:#666666;font-size:16px;">20</span><span style="font-family:宋体;color:#666666;font-size:16px;">多年丰富医疗经验的结晶。作者将一套整合了身、心、灵，以及生活风格的减压方案奉献给大家。《</span><span style="color:#666666;font-size:16px;">Lee</span><span style="font-family:宋体;color:#666666;font-size:16px;">博士减压法》中包括</span><span style="color:#666666;font-size:16px;">4</span><span style="font-family:宋体;color:#666666;font-size:16px;">个简单准确的测试（你的超压类型、超压对你身体的哪个部分伤害最大、你的复原力水平、你最近经历了哪些压力事件）、</span><span style="color:#666666;font-size:16px;">14</span><span style="font-family:宋体;color:#666666;font-size:16px;">天减压排毒食谱、</span><span style="color:#666666;font-size:16px;">4</span><span style="font-family:宋体;color:#666666;font-size:16px;">周超压释放方案。</span><span style="color:#666666;"><br />\r\n</span><span style="font-family:宋体;color:#666666;"> </span><span style="color:#666666;font-size:16px;"> </span><span style="font-family:宋体;color:#666666;font-size:16px;">《</span><span style="color:#666666;font-size:16px;">Lee</span><span style="font-family:宋体;color:#666666;font-size:16px;">博士减压法》强大的科学根基，整合性的减压理念，使它成为美国最受欢迎的减压书。</span> \r\n</p>', '', '', '', 1394185255, 0),
(96, 0, 3, ',', 47, 81, '&lt;&lt;高效能人士7个习惯&gt;&gt;', '<p class="MsoNormal" style="text-align:left;" align="left">\r\n	<span style="font-family:新宋体;font-size:16px;"><img src="/las/resource/attachment/images/3/2014/03/OfOw4SiI9PKtTPkcCx5p48SfjxpPMs.jpg" alt="" /><br />\r\n</span> \r\n</p>\r\n<p class="MsoNormal" style="text-align:left;" align="left">\r\n	<span style="font-family:新宋体;font-size:16px;">作者：</span><span style="line-height:200%;font-family:新宋体;font-size:16px;">(</span><span style="line-height:200%;font-family:新宋体;"><span style="font-size:16px;">美</span><span style="font-size:16px;">)</span><span style="font-size:16px;">史蒂芬</span><span style="font-size:16px;">.</span><span style="font-size:16px;">柯维</span></span><span style="line-height:200%;font-family:Verdana;"> <span></span></span> \r\n</p>\r\n<p class="MsoNormal" style="text-align:left;" align="left">\r\n	<span style="font-family:新宋体;font-size:16px;">内容简介：</span><span style="line-height:200%;font-family:新宋体;"><span style="font-size:16px;">《高效能人士的</span><span style="font-size:16px;">7</span><span style="font-size:16px;">个习惯》</span><span style="font-size:16px;">1989</span><span style="font-size:16px;">年在美国问世，一时掀起了全民阅读和培训的高潮，成为美国企业员工、公务员、军队的装备书，更成为仅次于《圣经》的影响力最大的作品。如今它的全球销量已超过</span><span style="font-size:16px;">2000</span><span style="font-size:16px;">万册。被福布斯评为“有史以来最具影响力的</span><span style="font-size:16px;">10</span><span style="font-size:16px;">大管理类书籍之一”。</span><span style="font-size:16px;">2003</span><span style="font-size:16px;">年</span><span style="font-size:16px;">4</span><span style="font-size:16px;">月，这《高效能人士的</span><span style="font-size:16px;">7</span><span style="font-size:16px;">个习惯》的俄文版在莫斯科上市，总统普京对媒体发表感慨说，“俄罗斯应该出现这样伟大的思想家”。他建议俄罗斯每一个公民都要阅读这《高效能人士的</span><span style="font-size:16px;">7</span><span style="font-size:16px;">个习惯》。柯维的思想和方法帮助数以百万计的人打造卓有成效的人生。这种卓有成效远远超过了物质的丰裕，更是一种道德人生和幸福人生。他提出的“全面成功才是真正成功”的思想和实践方法给人很大启示和指导：身处多元化的社会，光是事业无往不利，只能算成功了一半。唯有兼顾人生其他层面的圆融和谐，才是全面的成功。在柯维众多著作中，“高效能人士的七个习惯”是柯维思想的核心精神。其倡导的七个习惯是：</span><span style="font-size:16px;">1.</span><span style="font-size:16px;">主动积极：掌握选择的自由，对现实环境积极响应，才是真正对生命负责的人。</span><span style="font-size:16px;">2.</span><span style="font-size:16px;">以终为始：锁定生命的坐标，掌握真正的使命，凝聚向前的力量，并全力以赴。</span><span style="font-size:16px;">3.</span><span style="font-size:16px;">要事第一：忙要忙得有意义，辨别事情的轻重缓急，急所当急，是个人管理之钥。</span><span style="font-size:16px;">4.</span><span style="font-size:16px;">双赢思维：事情并不是只有二分法，非强即弱，非胜即败。人人都可以是赢家。</span><span style="font-size:16px;">5.</span><span style="font-size:16px;">知彼解己：聆听与表达同样重要，做个双向传播的聆听者，才是沟通高手。</span><span style="font-size:16px;">6.</span><span style="font-size:16px;">统合综效：唯有合作才能统合综效，人类的潜能因而激发，面对挑战也毫不畏惧。</span><span style="font-size:16px;">7.</span><span style="font-size:16px;">不断更新：人生最佳的投资策略，就是严格要求自己，掌握不断更新的成长原则。这些观念给人们特别是管理者带来一场意义非凡的变革。</span></span><span style="line-height:200%;font-family:Verdana;"> </span> \r\n</p>', '', '', '', 1394185381, 0);
